mapper.select:
SELECT T_ARTICLE.id, T_ARTICLE.title, T_ARTICLE.tag, T_ARTICLE.create_by, T_ARTICLE.update_by, T_ARTICLE.create_time, T_ARTICLE.update_time, T_ARTICLE.ORDER_NUM, ifnull(T_VIEWS.COUNT,0) as VIEW_COUNT FROM T_ARTICLE left join T_VIEWS on T_VIEWS.ARTICLE_ID = T_ARTICLE.ID WHERE T_ARTICLE.IS_DELETE = 0
AND T_ARTICLE.title LIKE #{title}
ORDER BY T_ARTICLE.ORDER_NUM DESC, T_ARTICLE.create_time DESC

select id,TITLE,CREATE_TIME,TAG from T_ARTICLE where IS_DELETE=0

    and T_ARTICLE.tag=#{code}

ORDER BY CREATE_TIME DESC
